INSERT INTO public.services (title, slug, short_description, long_description, icon, features, benefits, display_order) VALUES
('Web Development', 'web-development', 'Custom web applications built with modern frameworks and best practices.', '## Custom Web Development\n\nWe build fast, scalable web applications using cutting-edge technologies. From single-page apps to complex enterprise platforms, our team delivers clean, maintainable code that performs.\n\nOur development process ensures every project is built on a solid foundation with modern architecture patterns, comprehensive testing, and performance optimization.', 'Globe', '["Next.js & React", "TypeScript", "Responsive Design", "API Integration", "Performance Optimization", "SEO Best Practices"]', '["Faster load times", "Better user engagement", "Scalable architecture", "Lower maintenance costs"]', 1),
('UI/UX Design', 'ui-ux-design', 'Beautiful, intuitive interfaces designed to delight users and drive conversions.', '## UI/UX Design Services\n\nGreat design is more than aesthetics — it is about creating intuitive experiences that guide users toward their goals. We combine research, strategy, and creativity to deliver interfaces that look stunning and perform beautifully.\n\nFrom wireframes to polished prototypes, we ensure every pixel serves a purpose.', 'Palette', '["User Research", "Wireframing", "Prototyping", "Design Systems", "Usability Testing", "Brand Identity"]', '["Higher conversion rates", "Improved user satisfaction", "Consistent brand experience", "Reduced development time"]', 2),
('E-Commerce', 'e-commerce', 'Scalable online stores that convert visitors into customers.', '## E-Commerce Solutions\n\nWhether you need a Shopify store or a fully custom e-commerce platform, we build online stores that drive sales. Our solutions include payment integration, inventory management, and analytics to help you grow.\n\nWe focus on the entire customer journey — from landing page to checkout — optimizing every step for maximum conversion.', 'ShoppingCart', '["Shopify & Custom Stores", "Payment Integration", "Inventory Management", "Analytics Dashboard", "SEO Optimization", "Mobile Commerce"]', '["Increased sales", "Better customer experience", "Streamlined operations", "Data-driven decisions"]', 3),
('Digital Strategy', 'digital-strategy', 'Data-driven strategies to grow your digital presence and reach your goals.', '## Digital Strategy & Consulting\n\nWe help businesses navigate the digital landscape with clear, actionable strategies. From market analysis to growth hacking, our consultants work alongside your team to identify opportunities and execute plans that deliver measurable results.', 'TrendingUp', '["Market Analysis", "SEO Strategy", "Content Planning", "Growth Hacking", "Analytics & Reporting", "Competitor Analysis"]', '["Clear roadmap", "Measurable ROI", "Competitive advantage", "Sustainable growth"]', 4),
('Mobile Apps', 'mobile-apps', 'Native and cross-platform mobile applications for iOS and Android.', '## Mobile App Development\n\nWe create mobile applications that users love. Whether you need a native iOS/Android app or a cross-platform solution, we deliver polished, performant apps that work seamlessly across devices.\n\nOur mobile team specializes in React Native and Flutter for efficient cross-platform development without sacrificing quality.', 'Smartphone', '["React Native", "Flutter", "iOS & Android", "Push Notifications", "Offline Support", "App Store Optimization"]', '["Wider reach", "Better engagement", "Native performance", "Single codebase"]', 5),
('SEO & Marketing', 'seo-marketing', 'Comprehensive SEO and digital marketing to drive organic growth.', '## SEO & Digital Marketing\n\nVisibility is everything in the digital world. Our SEO and marketing team uses proven strategies to improve your search rankings, drive qualified traffic, and convert visitors into customers.\n\nWe combine technical SEO, content strategy, and data analytics to deliver sustainable organic growth.', 'TrendingUp', '["Technical SEO", "Content Marketing", "Link Building", "Local SEO", "PPC Management", "Social Media"]', '["Higher rankings", "More organic traffic", "Better conversion rates", "Brand authority"]', 6);
